// src/dashboard/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  Container, Grid, Card, CardContent, Typography, Button,
} from '@mui/material';
import { jwtDecode } from 'jwt-decode'; // Corrected import statement

const Dashboard = () => {
  const [items, setItems] = useState([]);
  const [userId, setUserId] = useState(null); // Store the logged-in user's ID
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');  // Check if user is logged in

  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items', error);
    }
  };

  const handleDelete = async (itemId) => {
    try {
      await axios.delete(`http://localhost:5000/items/${itemId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      fetchItems(); // Refresh the items after deletion
    } catch (error) {
      console.error('Error deleting item', error);
    }
  };

  // Get the logged-in user's ID from the JWT token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwtDecode(token);
      setUserId(decodedToken.sub); // Assuming `sub` is the user ID in the token
    }
    fetchItems();
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>

      {/* Show "Add New Item" button if authenticated, otherwise show "Login" button */}
      {isAuthenticated ? (
        <Button variant="contained" color="primary" onClick={() => navigate('/add-item')}>
          Add New Item
        </Button>
      ) : (
        <Button variant="contained" color="secondary" onClick={() => navigate('/login')}>
          Login
        </Button>
      )}

      <Grid container spacing={3} marginTop={2}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card>
              <CardContent>
                <Typography variant="h5">{item.title}</Typography>
                <Typography variant="body2">{item.description}</Typography>
                <Typography variant="caption">
                  {new Date(item.timestamp).toLocaleString()}
                </Typography>
                {/* Show Edit/Delete buttons only if the logged-in user is the creator of the item */}
                {isAuthenticated && userId === item.userId && (
                  <>
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => navigate(`/edit-item/${item.id}`)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => handleDelete(item.id)}
                    >
                      Delete
                    </Button>
                  </>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Dashboard;
