import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box } from '@mui/material';

const AddItemPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const handleAddItem = async (e) => {
    e.preventDefault(); // Prevent page refresh
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.log('Token not found, redirecting to login.');
        navigate('/login'); // Redirect to login if no token found
        return;
      }

      // Log token to check if it's valid
      console.log('Token found:', token);

      // Send POST request to backend with Authorization header
      const response = await axios.post(
        'http://localhost:5000/items',
        { title, description },
        {
          headers: {
            Authorization: `Bearer ${token}`, // Add the token in the Authorization header
            'Content-Type': 'application/json',
          },
        }
      );

      // Check the response
      console.log('Response from server:', response);

      if (response.status === 201) {
        console.log('Item successfully added. Navigating to dashboard.');
        navigate('/dashboard'); // Navigate to dashboard after successful item creation
      }
    } catch (error) {
      // Log detailed Axios error information
      console.error('Error adding item:', error);
      if (error.response) {
        console.error('Error response data:', error.response.data);
        console.error('Error status code:', error.response.status);
        console.error('Error headers:', error.response.headers);
      } else if (error.request) {
        console.error('Request made but no response:', error.request);
      } else {
        console.error('Error setting up the request:', error.message);
      }
    }
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>
        Add New Item
      </Typography>
      <Box component="form" onSubmit={handleAddItem}>
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          margin="normal"
          multiline
          rows={4}
          required
        />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Add Item
        </Button>
      </Box>
    </Container>
  );
};

export default AddItemPage;
