import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, MenuItem, Container, Typography } from '@mui/material';

const AddItemPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch categories from the backend
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories', error);
      }
    };

    fetchCategories();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !description || !selectedCategory) {
      setError('All fields are required');
      return;
    }

    const newItem = {
      title,
      description,
      categoryId: selectedCategory,  // Pass the selected category ID
    };

    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        setError('You must be logged in to add an item.');
        navigate('/login');
        return;
      }

      // Send the new item data to the backend
      await axios.post('http://localhost:5000/items', newItem, {
        headers: { 'user-id': userId },
      });

      // Redirect to the dashboard after item is added
      navigate('/dashboard');
    } catch (error) {
      console.error('Error adding item', error);
      setError('Failed to add item');
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Add New Item
      </Typography>

      {error && <Typography color="error">{error}</Typography>}

      <form onSubmit={handleSubmit}>
        <TextField
          label="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          label="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          fullWidth
          margin="normal"
          required
          multiline
          rows={4}
        />

        <TextField
          label="Category"
          select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          fullWidth
          margin="normal"
          required
        >
          {categories.map((category) => (
            <MenuItem key={category.id} value={category.id}>
              {category.name}
            </MenuItem>
          ))}
        </TextField>

        <Button variant="contained" color="primary" type="submit">
          Add Item
        </Button>
      </form>
    </Container>
  );
};

export default AddItem;
