import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { login } from '../auth/authSlice'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import the global CSS

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    try {
      // Dispatch the login action
      const resultAction = await dispatch(login({ email, password }));

      // Check if the action was successful
      if (login.fulfilled.match(resultAction)) {
        // Redirect to the home page or another route on successful login
        navigate('/');
      } else {
        // Handle errors from the server
        setError(resultAction.payload?.message || 'Invalid credentials');
      }
    } catch (err) {
      // Handle any unexpected errors
      console.error('Login error:', err);
      setError('Login failed, please try again.');
    }
  };

  return (
    <div className="container">
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <div className="error">{error}</div>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
