import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box } from '@mui/material';

const AddItemPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('userId'); // Check if userId exists in localStorage

  const handleAddItem = async (e) => {
    e.preventDefault();

    if (!isAuthenticated) {
      console.error('You are not logged in. Redirecting to login.');
      navigate('/login'); // Redirect if the user is not logged in
      return;
    }

    const userId = localStorage.getItem('userId'); // Get userId from localStorage

    try {
      const response = await axios.post(
        'http://localhost:5000/items/create',
        { title, description },
        {
          headers: { 'user-id': userId }, // Pass userId in headers instead of JWT
        }
      );

      if (response.status === 201) {
        navigate('/dashboard'); // Navigate after successful item creation
      }
    } catch (error) {
      console.error('Error adding item:', error);
      if (error.response) {
        console.error('Response data:', error.response.data);
        console.error('Response status:', error.response.status);
        console.error('Response headers:', error.response.headers);
      } else if (error.request) {
        console.error('Request made but no response received:', error.request);
      } else {
        console.error('Error setting up the request:', error.message);
      }
    }
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>
        Add New Item
      </Typography>
      <Box component="form" onSubmit={handleAddItem}>
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          margin="normal"
          multiline
          rows={4}
          required
        />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Add Item
        </Button>
      </Box>
    </Container>
  );
};

export default AddItemPage;
