// src/items/AddItemPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Box } from '@mui/material';

const AddItemPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const handleAddItem = async (e) => {
  e.preventDefault(); // Prevent page refresh
  try {
    const token = localStorage.getItem('token'); // Get the token from localStorage
    if (!token) {
      navigate('/login'); // Redirect to login if no token found
      return;
    }

    const response = await axios.post(
      'http://localhost:5000/items/create',
      { title, description },
      {
        headers: {
          Authorization: `Bearer ${token}`, // Add the token in the Authorization header
        },
      }
    );

    if (response.status === 201) {
      navigate('/'); // Navigate to dashboard after successful item creation
    }
  } catch (error) {
    // console.error('Error adding item', error); // Log the error for debugging
  }
};

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>
        Add New Item
      </Typography>
      <Box component="form" onSubmit={handleAddItem}>
        <TextField
          label="Title"
          variant="outlined"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          label="Description"
          variant="outlined"
          fullWidth
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          margin="normal"
          multiline
          rows={4}
          required
        />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Add Item
        </Button>
      </Box>
    </Container>
  );
};

export default AddItemPage;
