import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';


const api = axios.create({
  baseURL: 'http://localhost:5000', // Ensure this is the correct base URL
});


// Update the API URL to match the backend server's address
export const signup = createAsyncThunk('auth/signup', async (userData) => {
  const response = await axios.post('http://localhost:5000/auth/signup', userData); // Corrected URL
  return response.data;
});

// Async thunk for login
export const login = createAsyncThunk('auth/login', async (userData) => {
  const response = await axios.post(`http://localhost:5000/auth/login`, userData);
  return response.data;
});

const authSlice = createSlice({
  name: 'auth',
  initialState: {
    user: null,
    token: localStorage.getItem('token') || null,
    status: 'idle',
    error: null,
  },
  reducers: {
    logout: (state) => {
      state.user = null;
      state.token = null;
      localStorage.removeItem('token');
    },
    
  },
  extraReducers: (builder) => {
    builder
      .addCase(login.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(login.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.user = action.payload;
      })
      .addCase(login.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
