import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, Grid, Card, CardContent, Typography, Button } from '@mui/material';

const Dashboard = () => {
  const [items, setItems] = useState([]);
  const [userId, setUserId] = useState(null); 
  const [userName, setUserName] = useState('');  // Store the user's name
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('userId'); 
  console.log(items.map(item1))
  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items', error);
    }
  };

  const handleDelete = async (itemId) => {
    try {
      await axios.delete(`http://localhost:5000/items/${itemId}`);
      fetchItems(); 
    } catch (error) {
      console.error('Error deleting item', error);
    }
  };

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    const storedUserName = localStorage.getItem('userName');  // Fetch the user's name
    if (storedUserId) {
      setUserId(storedUserId);
      setUserName(storedUserName); // Set the user's name
    }
    fetchItems();
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Welcome, {userName}  {/* Display the user's name */}
      </Typography>

      {isAuthenticated ? (
        <Button variant="contained" color="primary" onClick={() => navigate('/add-item')}>
          Add New Item
        </Button>
      ) : (
        <Button variant="contained" color="secondary" onClick={() => navigate('/login')}>
          Login
        </Button>
      )}

      <Grid container spacing={3} marginTop={2}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card>
              <CardContent>
                <Typography variant="h5">{item.title}</Typography>
                <Typography variant="body2">{item.description}</Typography>
                <Typography variant="caption">
                  {new Date(item.timestamp).toLocaleString()}
                </Typography>

                {/* Only show Edit/Delete buttons if the logged-in user owns the item */}
                {isAuthenticated && userId === item.userId && (
                  <>
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => navigate(`/edit-item/${item.id}`)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => handleDelete(item.id)}
                    >
                      Delete
                    </Button>
                  </>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Dashboard;
