import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, Grid, Card, CardContent, Typography, Button, Box } from '@mui/material';

const Dashboard = () => {
  const [items, setItems] = useState([]);
  const [userId, setUserId] = useState(null);
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('userId'); // Check if user is authenticated

  // Fetch all items
  const fetchItems = async () => {
    try {
      const response = await axios.get('http://localhost:5000/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items', error);
    }
  };

  // Handle item deletion with userId passed in headers
  const handleDelete = async (itemId) => {
    try {
      const userId = localStorage.getItem('userId'); // Fetch userId from localStorage
      if (!userId) {
        console.error('User is not logged in');
        navigate('/login'); // Redirect if user is not authenticated
        return;
      }

      await axios.delete(`http://localhost:5000/items/${itemId}`, {
        headers: {
          'user-id': userId, // Pass userId in headers
        },
      });
      fetchItems(); // Refresh item list
    } catch (error) {
      console.error('Error deleting item', error);
    }
  };

  useEffect(() => {
    // Retrieve user info from local storage
    const storedUserId = localStorage.getItem('userId');
    const storedUserName = localStorage.getItem('userName');
    if (storedUserId) {
      setUserId(storedUserId);
      setUserName(storedUserName);
    }
    fetchItems(); // Fetch items when component mounts
  }, []);

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Welcome, {userName}
      </Typography>

      {isAuthenticated ? (
        <Button variant="contained" color="primary" onClick={() => navigate('/add-item')}>
          Add New Item
        </Button>
      ) : (
        <Button variant="contained" color="secondary" onClick={() => navigate('/login')}>
          Login
        </Button>
      )}

      <Grid container spacing={3} marginTop={2}>
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card>
              <CardContent>
                <Typography variant="h5">{item.title}</Typography>
                <Typography variant="body2">{item.description}</Typography>
                <Typography variant="caption">
                  {new Date(item.timestamp).toLocaleString()}
                </Typography>

                {/* Buttons with margin between them */}
                {isAuthenticated && userId == item.userId && (
                  <Box mt={2}> {/* Add margin to the top for spacing */}
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => navigate(`/edit-item/${item.id}`)}
                      style={{ marginRight: '10px' }} // Add spacing between buttons
                    >
                      Edit
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => handleDelete(item.id)}
                    >
                      Delete
                    </Button>
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Dashboard;
